const Browser: React.FC<BrowserProps> = ({ name }) => {
  let component: React.ReactNode = null;

  switch (name) {
    case 'chrome':
      component = Chrome();
      break;
    case 'firefox':
      component = <Firefox />;
      break;
    case 'safari':
      component = Safari();
      break;
    case 'edge':
      component = Edge();
      break;
  }

  return component;
};

export default Browser;
